import createStore from 'unistore';
import { string } from 'prop-types';
import axios from 'axios';

const initialState = {
    listProduct1:[],
    listProduct: [],
    product:[],
    cartdetail: '',
    cartProduct: [],
    cart:[],
    data_seller:[],
    userNameInput:"",
    userPasswordInput:"",
    UserFullName:"",
    userShop:"",
    userEmail:"",
    userAddress:"",
    userPhone:"",
    token:"",
    searchKeyword:"",
    isLoading:false,
    cekFotoStatus: false,
    linkAudio: "",
    fullName : "",
    is_login: false,
    //login
    usename : "",
    password: "",
    //
    image_user: "",
    name_user: "",
    rating_user:"",
    review_user:"",
    image_agent: "",
    name_agent: "",
    rating_agent:"",
    review_agent:"",
    role:"",
    transaksi:[],
    // Add Product
    nama_product:"",
    description:"",
    weight:"",
    price:"",
    reference:"",
    stock:"",
    halal:false,
    taste:"",
    image:"",
    //checkout
    checkout_Email:"",
    checkout_HP:"",
    checkout_Alamat:""

};
export const store = createStore(initialState)

export const actions = store => ({
    getSignIn : async (state) => {
        console.log("username",state.userNameInput)
        console.log("password",state.userPasswordInput)
        const username = state.userNameInput
        const password = state.userPasswordInput
        const mydata = {
            "username": username,
            "password": password
        };
        const req = {
            method: "get",
            url: "https://cari-nama-domain-anda.site/login?username="+username+"&password="+password
          };
          const self = this
          await axios(req)
          .then(function(response){
                console.log("token === ", response.data.token)
                console.log("password === ", password)
                console.log("login = ", store.is_login)
                if (response.data.token !== null ){
                    localStorage.setItem("token", response.data.token);
                    store.setState({token : response.data.token, 
                                    is_login : true, 
                                    username : state.userNameInput,
                                    password : state.userPasswordInput})
                    self.props.history.push("/")              
                    alert("login success")
                }
            })
            .catch(function(error){
                store.setState({isLoading: false})
                // handle error
            })
    },

    getProfileUser : async (state) => {
        // console.log("tes",localStorage.getItem('token'))
        const req = {
            method: "get",
            url: "https://cari-nama-domain-anda.site/user",
            headers: {
                Authorization: "Bearer " + localStorage.getItem("token")
              }
          };
          console.log()
          const self = this
          await axios(req)
          .then(function(response){
                console.log("id === ", response.data.id)
                if (response.data.id !== null ){
                    localStorage.setItem("role", response.data.role);
                    localStorage.setItem("username", response.data.username);
                    localStorage.setItem("fullname", response.data.fullname);
                    localStorage.setItem("phone_number", response.data.number_phone);
                    localStorage.setItem("address", response.data.address);
                    localStorage.setItem("email", response.data.email);
                    localStorage.setItem("city", response.data.city);
                    localStorage.setItem("image", response.data.image);
                    self.props.history.push("/")               
                    alert("login success")
                }
            })
            .catch(function(error){
                // handle error
            })
    },

    getProfileAgent : async (state) => {
        // console.log("tes",localStorage.getItem('token'))
        const req = {
            method: "get",
            url: "https://cari-nama-domain-anda.site/agent",
            headers: {
                Authorization: "Bearer " + localStorage.getItem("token")
              }
          };
          console.log()
          const self = this
          await axios(req)
          .then(function(response){
                console.log("id === ", response.data.id)
                if (response.data.id !== null ){
                    localStorage.setItem("role", response.data.role);
                    localStorage.setItem("username", response.data.username);
                    localStorage.setItem("fullname", response.data.fullname);
                    localStorage.setItem("phone_number", response.data.number_phone);
                    localStorage.setItem("address", response.data.address);
                    localStorage.setItem("email", response.data.email);
                    localStorage.setItem("city", response.data.city);
                    localStorage.setItem("image", response.data.image);
                    self.props.history.push("/")               
                    alert("login success")
                }
            })
            .catch(function(error){
                // handle error
            })
    },

    getProfileSeller : async (state) => {
        // console.log("tes",localStorage.getItem('token'))
        const req = {
            method: "get",
            url: "https://cari-nama-domain-anda.site/seller",
            headers: {
                Authorization: "Bearer " + localStorage.getItem("token")
              }
          };
          console.log()
          const self = this
          await axios(req)
          .then(function(response){
                console.log("id === ", response.data.id)
                if (response.data.id !== null ){
                    localStorage.setItem("role", response.data.role);
                    localStorage.setItem("username", response.data.username);
                    localStorage.setItem("shop_name", response.data.shop_name);
                    localStorage.setItem("fullname", response.data.fullname_owner);
                    localStorage.setItem("phone_number", response.data.number_phone);
                    localStorage.setItem("address", response.data.address);
                    localStorage.setItem("email", response.data.email);
                    localStorage.setItem("city", response.data.city);
                    localStorage.setItem("image", response.data.image);
                    self.props.history.push("/")               
                    alert("login success")
                }
            })
            .catch(function(error){
                // handle error
            })
    },

    postSignUp1 : async (state) => {
        const username = state.userNameInput
        const password = state.userPasswordInput
        const fullname = state.UserFullName
        const address = state.userAddress
        const email = state.userEmail
        const phone = state.userPhone
        const mydata = {
            "username": username,
            "password": password,
            "fullname": fullname,
            "email": email,
            "address": address,
            "number_phone": phone
        };

        const req = {
          method: "post",
          url: "https://cari-nama-domain-anda.site/user/registration",
          headers: {
            "Content-Type": "application/json"
          },
          data: mydata
        };

        await axios(req)
            .then(response => {
                  store.setState({
                      "is_login": true
                  });
              //   return response
            })
            .catch(error => {
                console.log(mydata)
                console.log(error)
                return false
        })
    },
    postSignUp2 : async (state) => {
        console.warn("username",state.userNameInput)
        console.warn("password",state.userPasswordInput)
        console.warn("fullname",state.UserFullName)
        console.warn("address",state.userAddress)
        console.warn("email",state.userEmail)
        console.warn("phone",state.userPhone)
        const username = state.userNameInput
        const password = state.userPasswordInput
        const fullname = state.UserFullName
        const address = state.userAddress
        const email = state.userEmail
        const phone = state.userPhone
        const mydata = {
            "username": username,
            "password": password,
            "fullname": fullname,
            "email": email,
            "address": address,
            "number_phone": phone
        };

        const req = {
          method: "post",
          url: "https://cari-nama-domain-anda.site/agent/registration",
          headers: {
            "Content-Type": "application/json"
          },
          data: mydata
        };
        const self = this
        await axios(req)
        .then(function(response){
            store.setState({token : response.data.token, is_login : true})
            self.props.history.push("/")               
                alert("login success")
        })
        .catch(function(error){
            store.setState({isLoading: false})
            // handle error
        })

    },

    postSignUp3 : async (state) => {
        console.warn("username",state.userNameInput)
        console.warn("password",state.userPasswordInput)
        console.warn("toko",state.userShop)
        console.warn("fullname",state.UserFullName)
        console.warn("address",state.userAddress)
        console.warn("email",state.userEmail)
        console.warn("phone",state.userPhone)
        const username = state.userNameInput
        const password = state.userPasswordInput
        const shop = state.userShop
        const fullname = state.UserFullName
        const address = state.userAddress
